const API_BASE_URL = import.meta.env.VITE_API_URL || '';

export interface ApiResponse<T> {
  data: T;
  error?: string;
}

async function request<T>(
  method: string,
  path: string,
  body?: Record<string, unknown>,
): Promise<ApiResponse<T>> {
  const token = localStorage.getItem('token');
  const headers: Record<string, string> = {
    'Content-Type': 'application/json',
  };
  if (token) {
    headers['Authorization'] = `Bearer ${token}`;
  }
  const res = await fetch(`${API_BASE_URL}${path}`, {
    method,
    headers,
    body: body ? JSON.stringify(body) : undefined,
  });
  return res.json();
}

export async function login(params: { email: string; password: string }) {
  return request<unknown>('POST', '/api/login', params as unknown as Record<string, unknown>);
}

export async function forgotPassword(params: { email: string }) {
  return request<unknown>('POST', '/api/forgot-password', params as unknown as Record<string, unknown>);
}
